import './App.css';
import { useEffect, useReducer } from "react";
import Header from './components/Header';
import Loading from './components/Loading';
import Error from './components/Error';
import QuizMain from './components/QuizMain';
import EndQuiz from './components/EndQuiz';

const initialState = {
  quizQuestion: [],
  status: 'loading', // default to loading initially
  error: null // for error handling
};

function reducer(state, action) {
  switch (action.type) {
    case 'fetch_success':
      return {
        ...state,
        quizQuestion: action.payload,
        status: 'ready' // set the status to ready once data is fetched
      };
    case 'fetch_error':
      return {
        ...state,
        status: 'error', // set the status to error
        error: action.payload // handle error message
      };
    case 'start_quiz':
      return {
        ...state,
        status: 'active' // start quiz
      };
    case 'finish_quiz':
      return {
        ...state,
        status: 'finished' // finish quiz
      };
    default:
      return state; // default case to return the current state
  }
}

export default function App() {
  const [state, dispatch] = useReducer(reducer, initialState);

  const { quizQuestion, status } = state;

  const TotalQuestion = quizQuestion.length;

  useEffect(() => {
    fetch('http://localhost:8000/quizQuestion')
      .then((res) => res.json())
      .then((data) => {
        dispatch({ type: 'fetch_success', payload: data });
      })
      .catch((err) => {
        dispatch({ type: 'fetch_error', payload: err.message });
      });
  }, []);

  // Function to handle starting the quiz
  const handleStartQuiz = () => {
    dispatch({ type: 'start_quiz' }); // Dispatch action to start quiz
  };

  // Render based on different states
  return (
    <>
      {status === 'loading' && <Loading />}
      {status === 'error' && <Error message={state.error} />}
      {status === 'ready' && <Header TotalQuestion={TotalQuestion} onStartQuiz={handleStartQuiz} />}
      {status === 'active' && (
        <>
          <QuizMain quizQuestion={state.quizQuestion} />
          <Qu quizQuestion={state.quizQuestion} />
          <QuizMain quizQuestion={state.quizQuestion} />

        </>
      )}
      {status === 'finished' && <EndQuiz />}
    </>
  );
}
